import React from 'react'

const CharacterGrid = ({ items, isLoading }) => {
  return  isLoading ? (
     <h1>Loading...</h1> 
     ) : ( 
     <section>
    {items.map(item => (<div>
      <h4 key = {item.char_id}>{item.name}</h4>
      <img src ={item.img} style={{height:200, width: 150, margin: 4, borderRadius: 15}} />
      </div>
    ))}
  </section>
  )
}

export default CharacterGrid
