import React from 'react';
import { Bar, } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const bar = () => {

  return (
    <div>
      <Bar
        height={400}
        width={1000}
        data={{
          labels:['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange', 'Black'],
          datasets:[{
            label:'Votes',
            data:[12,19,8,15,4,9,17],
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)',
              'rgba(0, 0, 0, 0.2)'
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
              'rgba(0, 0, 0, 1)'
            ],
            borderWidth: 1
          }]
        }}
        options={{
          options: {
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
        }}
         />
    </div>
  )
}

export default bar
