import React, {useState} from 'react';

const TextForm = (props) => {
  const [text, setText] = useState(''); 

  const handleUpClick =()=> {
    let newText = text.toUpperCase();
    setText(newText)
    props.showAlert("Converted to uppercase!","success");
  }
  const handleLoClick = ()=>{ 
    let newText = text.toLowerCase();
    setText(newText)
    props.showAlert("Converted to lowercase!", "success");
  }

  const handleClearClick = ()=>{ 
    let newText = '';
    setText(newText);
    props.showAlert("Text Cleared!", "success");
  }

  const handleOnChange = (event)=>{
    setText(event.target.value) 
  }
  const handleCopy = () => {
    navigator.clipboard.writeText(text); 
    props.showAlert("Copied to Clipboard!", "success");
  }
  const handleExtraSpaces = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
    props.showAlert("Extra spaces removed!", "success");
  }

  return <div style={{color: props.mode==='dark'?'white':'#042743'}}>
    <h1>Text-Utils </h1>
    <h2>A platform to analyze your text and play around it.</h2>
    <div className={`form-check form-switch text-${props.mode==='light'?'dark':'light'}`}>
      <input className="form-check-input" onClick={props.toggleMode} type="checkbox" id="flexSwitchCheckDefault"/>
      <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Enable DarkMode</label>
    </div>
    <div class="container mb-3 my-4">
      <textarea class="form-control" id="exampleFormControlTextarea1" rows="8"></textarea>
    </div>
    <button type="button" class="btn me-2 btn-primary">ToUpperCase</button>
    <button type="button" class="btn me-2 btn-primary">ToLowerCase</button>
    <button type="button" class="btn me-2 btn-primary">Copy</button>
    <button type="button" class="btn me-2 btn-primary">Clear</button>
    <button type="button" class="btn me-2 btn-primary">x</button>
    <button type="button" class="btn me-2 btn-primary">Primary</button>
    <div></div>
  </div>;
};

export default TextForm;
